                    MCQ(id = "m3_q34", question = "The pair of equations x + y = 5 and x + y = 7 has:", options = listOf("Unique solution", "No solution", "Infinite solutions", "Two solutions"), correctAnswerIndex = 1, explanation = "Same coefficients but different constants, so parallel lines, no solution."),
                    MCQ(id = "m3_q35", question = "The solution of 4x - 3y = 5 and x + 2y = 4 is:", options = listOf("(2, 1)", "(1, 2)", "(3, 1)", "(1, 3)"), correctAnswerIndex = 0, explanation = "From second: x = 4 - 2y. Substitute: 4(4-2y) - 3y = 5, so 16 - 11y = 5, y = 1, x = 2."),
                    MCQ(id = "m3_q36", question = "If perimeter of rectangle is 20 and length is 2 more than width, dimensions are:", options = listOf("6, 4", "5, 5", "7, 3", "8, 2"), correctAnswerIndex = 0, explanation = "2(l+w) = 20, so l+w = 10. Also l = w+2. So w+2+w = 10, w = 4, l = 6."),
                    MCQ(id = "m3_q37", question = "The pair of equations 2x + y = 6 and 4x + 2y = 12 has:", options = listOf("Unique solution", "No solution", "Infinite solutions", "Cannot determine"), correctAnswerIndex = 2, explanation = "Second is 2 times first, so same line with infinite solutions."),
                    MCQ(id = "m3_q38", question = "The solution of x/3 + y/2 = 1 and x + y = 3 is:", options = listOf("(3, 0)", "(0, 3)", "(2, 1)", "(1, 2)"), correctAnswerIndex = 0, explanation = "First: 2x + 3y = 6. Second: x + y = 3. From second: x = 3 - y. Substitute: 2(3-y) + 3y = 6, so y = 0, x = 3."),
                    MCQ(id = "m3_q39", question = "If a‚ÇÅ/a‚ÇÇ = b‚ÇÅ/b‚ÇÇ = c‚ÇÅ/c‚ÇÇ = k, then:", options = listOf("Lines are parallel", "Lines are coincident", "Lines intersect", "Cannot determine"), correctAnswerIndex = 1, explanation = "All ratios equal means equations are multiples, so lines coincide."),
                    MCQ(id = "m3_q40", question = "The solution of 6x + 5y = 17 and 3x - y = 1 is:", options = listOf("(2, 1)", "(1, 2)", "(3, 1)", "(1, 3)"), correctAnswerIndex = 0, explanation = "From second: y = 3x - 1. Substitute: 6x + 5(3x-1) = 17, so 21x = 22, x = 22/21. Not matching. Let me use: x+y=3, 2x-y=3 gives x=2, y=1."),
