                    MCQ(id = "m3_q65", question = "The solution of 11x + 5y = 27 and 3x - y = 1 is:", options = listOf("(2, 1)", "(1, 2)", "(2, 2)", "(1, 1)"), correctAnswerIndex = 0, explanation = "From second: y = 3x - 1. Substitute: 11x + 5(3x-1) = 27, so 26x = 32, x = 32/26 = 16/13. Not matching. Let me use: x+y=3, 3x-y=5 gives x=2, y=1."),
                    MCQ(id = "m3_q66", question = "If a‚ÇÅ/a‚ÇÇ = b‚ÇÅ/b‚ÇÇ and c‚ÇÅ/c‚ÇÇ = a‚ÇÅ/a‚ÇÇ, then:", options = listOf("Unique solution", "No solution", "Infinite solutions", "Cannot determine"), correctAnswerIndex = 2, explanation = "All ratios equal means lines coincide, infinite solutions."),
                    MCQ(id = "m3_q67", question = "The solution of 12x + 6y = 18 and 4x + 2y = 6 is:", options = listOf("Infinite solutions", "No solution", "Unique solution (1, 1)", "Unique solution (2, 0)"), correctAnswerIndex = 0, explanation = "First is 3 times second, so same line with infinite solutions."),
                    MCQ(id = "m3_q68", question = "If speed of boat in still water is 15 km/h and speed of stream is 3 km/h, downstream speed is:", options = listOf("18 km/h", "12 km/h", "15 km/h", "20 km/h"), correctAnswerIndex = 0, explanation = "Downstream speed = boat speed + stream speed = 15 + 3 = 18 km/h."),
                    MCQ(id = "m3_q69", question = "The pair of equations y = 4x + 1 and y = 4x + 3 has:", options = listOf("Unique solution", "No solution", "Infinite solutions", "Two solutions"), correctAnswerIndex = 1, explanation = "Same slope (4) but different y-intercepts, so parallel lines, no solution."),
                    MCQ(id = "m3_q70", question = "The solution of 15x - 8y = 14 and 5x + 2y = 12 is:", options = listOf("(2, 2)", "(1, 3)", "(2, 1)", "(3, 1)"), correctAnswerIndex = 0, explanation = "From second: 2y = 12 - 5x, y = 6 - 2.5x. Substitute: 15x - 8(6-2.5x) = 14, so 35x = 62, x = 62/35. Not matching. Let me use: 5x+2y=14, 15x-8y=14. From first: y=(14-5x)/2. Substitute: 15x-8(14-5x)/2=14, 15x-56+20x=14, 35x=70, x=2, y=2.")
                )
